function goPos(pos) {
 var offset = $('.postop'+pos);
 var pos = offset.offset();
 if( pos == undefined ) {
 alert('더 이상 찾을 수 없습니다');
 return false;
 }
 $('html, body').animate({scrollTop : pos.top}, 500);
}
function goPostop(pos) {
 var offset = $('.postop'+pos);
 var pos = offset.offset();
 if( pos != undefined ) {
 $('html, body').animate({scrollTop : pos.top}, 20);
 }
}
